INSERT INTO `lang2` VALUES ('fr','_template','atutor_available','ATutor est un produit gratuit disponible pour un usage limité selon les termes du <a href=\"http://www.gnu.org/licenses/licenses.html#GPL\">GNU le Permis Public</a>, qui autorise la distribution et la modification du code de source fourni, à condition que toutes les mentions de « copyright » et de droits d\'auteur restent inchangées. Le <a href=\"http://sourceforge.net/project/showfiles.php?group_id=21294\">code  source de ATutor</a> peut être téléchargé du site Sourceforge.net. Les demandes de rensiegnement concernant l\'usage commercial de ATutor devraient être adressées à <a href=\"mailto:info@atutor.ca\">info@atutor.ca</a>.','2003-08-07 21:49:57');
