INSERT INTO `lang2` VALUES ('es','_template','lc_question_text','La marca de pregunta le cuestiona la verdad o validez de una declaración o teoría. Preguntése a sí mismo: ¿Esto es realmente cierto?. ¿Porqué piensa que no es cierto?. ¿Porqué piensa que alguien piensa que sí lo es?.','2003-07-08 00:00:10');
