INSERT INTO `lang_base` VALUES ('_msgs','AT_HELP_CREATE_LINKS','Create categories in which to sort links by entering topic names in to the \"<strong><em>New Category</em></strong>\" field below.','2003-05-16 13:46:32','help msg');
