INSERT INTO `lang_base` VALUES ('_msgs','AT_HELP_FILEMANAGER4','To <strong><em>find the path</em></strong> to a course file, find the file in the File Manager then notice the \"Path to Current Directory\" information. The path after \"Home/\" is the path from the content to the File Manager content.<br /><br />','2003-10-24 13:52:47','help msg');
INSERT INTO `lang_base` VALUES ('_msgs','AT_HELP_CUSTOM_HEADER','Insert HTML formatted content to create a custom header for this course. Or, click on \"Load Default Wrap-around Template\", then modify the template with your own content.','2003-05-16 13:46:32','help msg');
