INSERT INTO `lang_base` VALUES ('_msgs','AT_HELP_DEMO_HELP','This is a demo Context Sensitive Help. Hover over it with a mouse pointer, or click on it, to display the accompanying help.','2003-05-16 13:46:32','help msg');
INSERT INTO `lang_base` VALUES ('_msgs','AT_HELP_DEMO_HELP2','This is a demo Help Box. When the Help Box preference is disabled, the Help Icon becomes clickable, so Help Boxes can be opened and close.','2003-05-16 13:46:32','help msg');
