INSERT INTO `lang_base` VALUES ('_msgs','AT_HELP_THEME_OPTIONS','Choose the colours and fonts that are easiest for you to view. Choose the <strong><em>Course Default Colours</em></strong>, Set Preferences for the current session, then save those settings to <em>This Course Only</em> so this course gets displayed with its own custom look-and-feel (if one has been created by the instructor).','2003-10-24 15:29:43','help msg');
